import React from "react";
import { CurrentUserContext } from "../../contexts/CurrentUserContext";
import PopupWithForm from "./PopupWithForm";

export default function AddCardPopup({ isOpen, onClose, onAddCard }) {
    const newCardRef = React.useRef();
    console.log(newCardRef)

    function handleSubmit() {
        onAddCard({
            name: newCardRef.current.value,
            link: newCardRef.current.value
        })
    }

    return(
        <CurrentUserContext.Provider>
            <PopupWithForm
                title="Новое место"
                name="add"
                isOpen={isOpen}
                onClose={onClose}
                onSubmit={handleSubmit}
            >
                <input ref={newCardRef} id="title" className="popup__input popup__input_value_title" type="text" placeholder="Название"
                       name="name" minLength="2" maxLength="30" required />
                <span id="title-error" className="popup__input-error" />
                <input ref={newCardRef} id="link" className="popup__input popup__input_value_link" type="url"
                       placeholder="Ссылка на картинку" name="link" required />
                <span id="link-error" className="popup__input-error" />
                <button className="popup__button popup__button_add" type="submit">Создать</button>
            </PopupWithForm>
        </CurrentUserContext.Provider>
    )
}
